// PatientList.js

import React, { useState } from "react";
import patientsData from "./patient.json";
import "./PatientList.css"; // Import the CSS file for styling
import Search from "./Search";

const PatientList = (props) => {
  const patientsPerPage = 10;
  const [currentPage, setCurrentPage] = useState(1);

  // Calculate the index range of patients to display on the current page
  const indexOfLastPatient = currentPage * patientsPerPage;
  const indexOfFirstPatient = indexOfLastPatient - patientsPerPage;
  const currentPatients = patientsData.patients.slice(
    indexOfFirstPatient,
    indexOfLastPatient
  );

  const totalPages = Math.ceil(patientsData.patients.length / patientsPerPage);

  const renderPagination = () => {
    const pageNumbers = [];

    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(
        <li
          key={i}
          className={i === currentPage ? "active" : ""}
          onClick={() => setCurrentPage(i)}
        >
          {i}
        </li>
      );
    }

    return <ul className="pagination">{pageNumbers}</ul>;
  };

  return (
    <div className="patient-list-container">
      <table className="patient-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>{Search }</th>
            <th>Date of Birth</th>
            <th>Blood Type</th>
            <th>Transfused in the last three months</th>
          </tr>
        </thead>
        <tbody>
          {currentPatients.map((patient) => (
            <tr key={patient.id}>
              <td>{`${patient.firstName} ${patient.lastName}`}</td>
              <td>{patient.dob}</td>
              <td>{patient.bloodType}</td>
              <td>{patient.transfusedLastThreeMonths ? "Yes" : "No"}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {renderPagination()}
    </div>
  );
};

export default PatientList;
