import React, { useEffect } from 'react';

const Main = ({ location }) => {
  useEffect(() => {
    // Use the location parameter to determine the route and scroll to the corresponding card
    const cardId = location.pathname.substring(location.pathname.lastIndexOf('/') + 1);
    const element = document.getElementById(cardId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  }, [location.pathname]);

  return (
    <>
      <div className="mainn">
        <div className="card" style={{ width: '21rem' }} id="left">
          {/* Card content */}
        </div>
        <div className="card" style={{ width: '21rem' }} id="middle">
          {/* Card content */}
        </div>
        <div className="card" style={{ width: '21rem' }} id="right">
          {/* Card content */}
        </div>
      </div>
    </>
  );
};

export default Main;
